import moment from 'moment';
import { setInterval, clearInterval } from 'timers';
import axios from 'axios';
import Swal from 'sweetalert2';
import datetime from 'vuejs-datetimepicker';
import jwt_decode from 'jwt-decode';

export default {

    name: 'TimeManager',

    components: {
        datetime
    },

    data() {
        return {
            today: moment().format('LLLL'),
            interval: null,
            chrono: null,
            arrival: null,
            departure: null,
            statusClock: false,
            finalChrono: null,
            setDeparture: null,
            setArrival: null,
            clocks: {
                status: '',
                time: ''
            }
        }
    },
    created(){

    },
    methods:{
        postClock(){

            if (this.chrono == null){

                this.statusClock = true;
                const now = moment.now();
                this.arrival = moment().toISOString().substring(0, moment().toISOString().length -1)
                this.setArrival = this.arrival
                this.clocks = {
                    status: true,
                    time: moment().toISOString().substring(0, moment().toISOString().length -1)
                };

                console.log(this.clocks.time)
                axios.patch('http://localhost:3000/api/clocks/user/' + jwt_decode(localStorage.getItem('token')).id,  this.clocks, {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem('token')
                    }
                })
                .then((response) => {
                    console.log(response);
                })
                .catch((error) => {
                    console.log(error);
                });

                this.interval = setInterval(()=> {
                    this.chrono = moment(moment().diff(now)).format("HH:mm:ss")
                }, 1000);

            }else {
                this.finalChrono = this.chrono;

                this.chrono = null;
                this.statusClock = false;
                this.departure = moment().toISOString().substring(0, moment().toISOString().length -1);
                this.setDeparture = this.departure;
                   this.clocks = {
                    status: false,
                    time: moment().toISOString().substring(0, moment().toISOString().length -1)
                };

                axios.patch('http://localhost:3000/api/clocks/user/' + jwt_decode(localStorage.getItem('token')).id,  this.clocks, {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem('token')
                    }
                })
                .then((response) => {
                    console.log(response);
                })
                .catch((error) => {
                    console.log(error);
                })

                clearInterval(this.interval);
                Swal.fire("Vous avez travaillez " + this.finalChrono + " aujourd'hui");
            }

        },

        setWorkingTime() {

            if (this.arrival != this.setArrival && this.departure != this.setDeparture) {

                this.clocks = {
                    status: true,
                    time: moment().toISOString().substring(0, this.setArrival.toISOString().length -1)
                };

                axios.patch('http://localhost:3000/api/clocks/user/' + jwt_decode(localStorage.getItem('token')).id,  this.clocks, {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem('token')
                    }
                })
                .then((response) => {
                    console.log(response);
                })
                .catch((error) => {
                    console.log(error);
                })

                this.clocks = {
                    status: false,
                    time: moment().toISOString().substring(0, this.setDeparture.toISOString().length -1)
                };

                axios.patch('http://localhost:3000/api/clocks/user/' + jwt_decode(localStorage.getItem('token')).id,  this.clocks, {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem('token')
                    }
                })
                .then((response) => {
                    console.log(response);
                })
                .catch((error) => {
                    console.log(error);
                })
            } else {
                alert('VOUS NE POUVEZ PAS MODIFIER')
            }

        }
    }





}
