import axios from 'axios';
import Swal from 'sweetalert2';
import jwt_decode from 'jwt-decode';

export default {

  name: "EditProfileForm",

  props: {
    dataBackgroundColor: {
      type: String,
      default: ""
    }
  },

  data() {
    return {
      errors: [],
      currentUser: {},
      user: {},
      id: null,
      lastname: null,
      firstname: null,
      email: null,
      password: null,
      created_at: null,
      updated_at: null,
    };
  },

  created() {
    this.fetchData();
  },

  methods: {

    fetchData() {

      axios.get('http://localhost:3000/api/users/' + jwt_decode(localStorage.getItem('token')).id, {
        headers: {
          Authorization: 'Bearer ' + localStorage.getItem('token')
        }
      })
      .then((response) => {
          this.currentUser = response.data;
          this.firstname = this.currentUser.first_name;
          this.lastname = this.currentUser.last_name;
          this.email = this.currentUser.email;
          this.password = this.currentUser.password;
          this.updated_at = this.currentUser.updatedAt;
          this.created_at = this.currentUser.createdAt;
      })
      .catch((error) => {
          console.log(error);
      })
    },

    edit() {

      if (this.password == null) {
        Swal.fire({
          type: 'error',
          title: 'Erreur',
          text: 'Veuillez saisir le mot de passe',
          footer: 'Merci de réessayez'
        });
      } else {
        this.user.first_name = this.firstname;
      this.user.last_name = this.lastname;
      this.user.password = this.password;
      this.user.email = this.email;

      console.log(this.currentUser);

      axios.put('http://localhost:3000/api/users/' + jwt_decode(localStorage.getItem('token')).id, this.user, {
        headers: {
          Authorization: 'Bearer ' + localStorage.getItem('token')
        }
      })
      .then((response) => {
          Swal.fire(
            'Félicitation',
            'Profil mis à jour!',
            'success'
          );
      })
      .catch((e) => {
        Swal.fire({
          type: 'error',
          title: 'Erreur',
          text: 'Mise à jour du compte impossible',
          footer: 'Merci de réessayez'
        });
        console.log(e);
      })
      }


    },

    remove() {
      Swal.fire({
                title: 'Êtes vous sûr de vouloir supprimer votre compte ?',
                type: 'warning',
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Oui supprimer'
                }).then((result) => {
                if (result.value) {
                    axios.delete('http://localhost:3000/api/users/' + jwt_decode(localStorage.getItem('token')).id, {
                        headers: {
                            Authorization: 'Bearer ' + localStorage.getItem('token')
                        }
                    }).then(response => {
                        Swal.fire(
                            'Supprimé!',
                            'success'
                        )
                        window.location.href="http://localhost:8080/login"
                    }).catch(err => {
                        Swal.fire({
                            type: 'error',
                            title: 'Erreur',
                            text: 'Suppression impossible'
                        })
                        console.log(err);
                    });
                }
            })
    }
  }
};
