import axios from 'axios';
import Swal from 'sweetalert2';
import jwt_decode from 'jwt-decode';

export default {

  name: 'TeamGraph',

  data() {
    return {
      allTeams: [],
      myTeams: [],
      name: '',
      nameState: 'null'
    }
  },

  props: {
    dataBackgroundColor: {
      type: String,
      default: ""
    }
  },

  created() {
    this.getTeams();
  },

  methods: {
    edit(id) {//FAIRE LE MODAL
      axios.put('http://localhost:3000/api/teams/' + id, {/*OBJET*/}, {
        headers: {
          Authorization: 'Bearer ' + localStorage.getItem('token')
        }
      })
      .then((response) => {
        Swal.fire(
                    'Félicitation!',
                    'Team modifiée avec succès',
                    'success'
        )
      })
      .catch((error) => {
          Swal.fire({
              type: 'error',
              title: 'Erreur',
              text: 'Modification impossible'
          })
      });
    },

    remove(id) {
      Swal.fire({
                title: 'Êtes vous sûr de vouloir supprimer cette team ?',
                type: 'warning',
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Oui supprimer'
                }).then((result) => {
                if (result.value) {
                    axios.delete('http://localhost:3000/api/teams/' + id, {
                        headers: {
                            Authorization: 'Bearer ' + localStorage.getItem('token')
                        }
                    }).then(response => {
                        this.getTeams();
                        Swal.fire(
                            'Supprimé!',
                            'success'
                        )
                    }).catch(err => {
                        Swal.fire({
                            type: 'error',
                            title: 'Erreur',
                            text: 'Suppression impossible'
                        })
                        console.log(id);
                    });
                }
            })
    },

    getTeams() {
      axios.get('http://localhost:3000/api/teams', {
      headers: {
        Authorization: 'Bearer ' + localStorage.getItem('token')
      }
    })
    .then((response) => {
      this.allTeams = response.data;

      if (jwt_decode(localStorage.getItem('token')).role == 'Administrator') {
        this.myTeams = this.allTeams;
      } else if (jwt_decode(localStorage.getItem('token')).role == 'Manager') {
        for (var i = 0; i < this.allTeams.length; i=i+1) {
          if (this.allTeams[i].managerId == jwt_decode(localStorage.getItem('token')).id) {
            this.myTeams.push(this.allTeams[i]);
          }
        }
      }
    })
    .catch((error) => {
      console.log(error);
    })
  }
  }
};
